import React from 'react'
import {uid} from 'react-uid'
import { Container, Button } from 'reactstrap'
import {setPatientChatPage} from "../../../../actions/message";
import {Link} from "react-router-dom";


class VisitHistory extends React.Component {

    constructor(props) {
        super(props)
        this.app = props.app
        this.state = {
            history: props.visits
        }
    }

    displayHistory() {
        if (this.state.history.length > 1) {
            const visits = this.state.history.map(function(visit) {
                return (
                    <li key={uid(visit)}>
                        <h5>Dr. {visit.username} ({visit.doctorType})</h5>
                        <button id={visit.username}
                                onClick={(event) => setPatientChatPage(event.target.id, this.app, this.state.history)}>chat
                        </button>
                    </li>
                )
            })
            return (
                <ul className="visit">
                    {visits}
                </ul>
            )
        } else {
            return(<h3>No visit history currently!</h3>)
        }
    }

    render() {
        return (
            <Container className="listgrid">
                <div>
                    <h3 className="historyHeader">Visit History:</h3>
                    {this.displayHistory()}
                </div>
            </Container>
        )
    }

}

export default VisitHistory
