import React from "react";
import { Container } from "reactstrap";
import { DataTable, TableHeader, Button} from "react-mdl";
import { getPatients, updateWatchlist } from "../../../actions/doctor"
import Patient from "../Patient/Patient";
import "./styles.css";

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      keyword: "",
      found: [],
      patients: [],
      watchlist: props.app.state.currentDoctor.watchlist,
    };
    this.handleSearchClick = this.handleSearchClick.bind(this);
    this.handleAddClick = this.handleAddClick.bind(this);
  }

  handleChange = (event) => {
    this.setState({
      keyword: event.target.value,
    });
  };

  showSearch(found_patients) {

    return (
      <div>
        <br></br> <br></br>
        <DataTable className="dtable" shadow={0} rows={found_patients}>
          <TableHeader name="Id" tooltip="The id of patients">
            Id
          </TableHeader>
          <TableHeader name="name" tooltip="The name of patients">
            Name
          </TableHeader>
          <TableHeader name="province" tooltip="The province of patient(s)">
            Province
          </TableHeader>
          <TableHeader name="age" tooltip="The age of patient(s)">
            Age
          </TableHeader>
          <TableHeader name="province" tooltip="The gender of patient(s)">
            Gender
          </TableHeader>
          <TableHeader name="add">Add</TableHeader>
        </DataTable>
      </div>
    );
  }

  showWatchlist() {
    if (this.state.watchlist.length < 1) {
      return (<p>No Patient on watchlist at the moment!</p>)
    } else {
      return( 
        <ul>
        {this.state.watchlist.map((patient) => (
          <li key={patient.userId}>
            <Patient
              key={patient.userId}
              name={patient.name}
              userId={patient.userId}
              checkList={patient.checklist}
              province={patient.province}
            />
            <br></br>
            <br></br>
          </li>
        ))}
      </ul>
      )
    }
  }

  handleSearchClick() {
    // check if name or id matches
    getPatients(this)
  }


  render() {
    const {app} = this.props

    let found_patients = []
    if (this.state.keyword.length < 1) {
      found_patients =  this.state.patients.map((user) => {
        console.log(user)
        return (
        {name: `${user.username}`, 
        Id: `${user._id}`, 
        province: `${user.province}`,
        age: `${user.age}`,
        add: <Button raised colored ripple onClick={(event)=> updateWatchlist(this, event.target, app.state.currentDoctor, user)} >Add</Button>
        }
        )
        })
    } else {
      found_patients = this.state.patients.filter((patient) => (
        patient._id.includes(this.state.keyword) || patient.username.includes(this.state.keyword)
      ))
      found_patients = found_patients.map((user) => {
        return (
          {name: `${user.username}`, 
          Id: `${user._id}`, 
          province: `${user.province}`,
          age: `${user.age}`,
          add: <Button raised colored ripple onClick={(event)=> updateWatchlist(this, event.target, app.state.currentDoctor, user)} >Add</Button>
        }
        )
      })
    }


    return (
      <Container>
        <div className="SearchBar">
          <label>
            Search Patient:
            <input
              className="input"
              type="text"
              placeholder="Search a patient by username"
              value={this.state.keyword}
              onChange={this.handleChange}
            />
            <Button raised colored ripple
              className="button"
              onClick={() => this.handleSearchClick()}
            >
              Search
            </Button>
            {this.showSearch(found_patients)}
          </label>
        </div>
        <h3>Watchlist:</h3>
        {this.showWatchlist()}
      </Container>
    );
  }
}

export default SearchBar;
