import React from "react";
import { Container } from "reactstrap";
import { DataTable, TableHeader } from "react-mdl";
import { getPatients } from "../../../actions/doctor"
import "./styles.css";

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      keyword: "",
      found: [],
      search_result: null,
      patients: [],
    };
    this.handleClick = this.handleClick.bind(this);
    this.handleAdd = this.handleAdd.bind(this);
  }

  handleChange = (event) => {
    this.setState({
      keyword: event.target.value,
    });
  };

  showSearch() {

    return (
      <div>
        <br></br> <br></br>
        <DataTable className="dtable" shadow={0} rows={[this.state.found]}>
          <TableHeader name="userId" tooltip="The id of patients">
            Id
          </TableHeader>
          <TableHeader name="name" tooltip="The name of patients">
            Name
          </TableHeader>
          <TableHeader name="province" tooltip="The province of patient(s)">
            Province
          </TableHeader>
          <TableHeader name="age" tooltip="The age of patient(s)">
            Age
          </TableHeader>
          <TableHeader name="province" tooltip="The gender of patient(s)">
            Gender
          </TableHeader>
          <TableHeader name="add">action1</TableHeader>
        </DataTable>
      </div>
    );
  }

  handleClick() {
    // check if name or id matches
    getPatients(this)

    const found_patients = []
    if (this.state.keyword.length < 1) {
      this.state.found = this.state.patients.map((user) => {
        return (
        {name: <input type="text" value={user.username} onChange={this.handleNameChange}/>, 
        Id: user._id, 
        password: <input type="text" onChange={this.handlePasswordChange} />,
        province: <input type="text" value={user.province} onChange={this.handleProvinceChange} />,
        age: <input type="text" value={user.age} onChange={this.handleAgeChange} />,
        edit: <button onClick={()=> updatePatient(this, user)} >Edit</button>
        }
        )
      })
    } else {
      this.patients.filter((patient) => {
        if (patient._id === this.state.keyword || patient.username === this.state.keyword) {
          found_patients.push(patient)
        }
      })
      this.state.found = found_patients.map((user) => {
        return (
        {name: <input type="text" value={user.username} onChange={this.handleNameChange}/>, 
        Id: user._id, 
        password: <input type="text" onChange={this.handlePasswordChange} />,
        province: <input type="text" value={user.province} onChange={this.handleProvinceChange} />,
        age: <input type="text" value={user.age} onChange={this.handleAgeChange} />,
        edit: <button onClick={()=> updatePatient(this, user)} >Edit</button>
        }
        )
      })
    }


  }

  render() {
    return (
      <Container>
        <div className="SearchBar">
          <label>
            Search Patient:
            <input
              className="input"
              type="text"
              placeholder="Search a patient by username or id"
              value={this.state.keyword}
              onChange={this.handleChange}
            />
            <button
              className="button"
              type="text"
              value="Search"
              onClick={() => this.handleClick()}
            >
              Search
            </button>
            {this.showSearch()}
          </label>
        </div>
      </Container>
    );
  }
}

export default SearchBar;
