import React from "react";
import PatientHeader from "../../PatientDashboard/components/PatientHeader/PatientHeader";
// import Button from "@material-ui/core/Button";
import {Link} from "react-router-dom";
import "./styles.css";
import {doctorType} from "../../RegistrationInfo/infomation";
import ReactTable from 'react-table-v6';
import "react-table-v6/react-table.css";
import matchSorter from 'match-sorter';
import {getDoctors} from '../../../actions/doctor';
import {updatePatientCookie} from "../../../actions/message";

class PrePatientChat extends React.Component {

    constructor(props) {
        super(props);
        this.props.history.push("/ChatPage/PrePatientChat");
        this.state = {
            username: this.props.app.state.currentPatient.username,
            doctorInfo: []
        };
    };

    createOptions = (opt) => {
        let select = [];
        for (let i = 0; i < opt.length; i++) {
            select.push(<option value={`${opt[i]}`}>{opt[i]}</option>)
        }
        return select
    };

    componentDidMount() {
        getDoctors(this)
    }
    render() {
        const {history, app} = this.props;

        const columns = [
            {
                Header: 'Doctor Name',
                id: 'doctorName',
                filterable: true,
                accessor: 'username',
                filterMethod: (filter, rows) =>
                    matchSorter(rows, filter.value, {keys: ['doctorName']}),
                filterAll: true
            },
            {
                Header: 'Doctor Type',
                filterable: true,
                accessor: 'doctorType',
                filterMethod: (filter, row) => {
                    if (filter.value === "All") {
                        return true;
                    }
                    return row[filter.id] === filter.value;
                },
                Filter: ({filter, onChange}) =>
                    <select
                        onChange={event => onChange(event.target.value)}
                        style={{width: "100%"}}
                        value={filter ? filter.value : "All"}
                    >
                        <option value='All'>All</option>
                        {this.createOptions(doctorType)}
                    </select>
            },
            {
                Header: 'Location',
                id: 'location',
                filterable: true,
                accessor: 'location',
                filterMethod: (filter, rows) =>
                    matchSorter(rows, filter.value, {keys: ['location']}),
                filterAll: true
            },
            {
                Header: 'Chat With Doctor',
                sortable: false,
                accessor: 'username',
                Cell: (props) => (
                    <Link to={"/ChatPage/PatientChatPage"}>
                        <button id={props.value} onClick={event => {
                            updatePatientCookie(event.target.id, app)
                        }}>
                            Send Message
                        </button>
                    </Link>)
            },
        ];
        return (
            <div>
                <PatientHeader history={history} app={app} name={this.state.username}/>
                <div className="list-div">
                    <p className="list-header">Doctor List</p>
                </div>

                <ReactTable
                    className={`-highlight`}
                    columns={columns}
                    data={this.state.doctorInfo}
                    noDataText={`No Doctor Found`}
                >
                </ReactTable>
            </div>
        );
    }
}

export default PrePatientChat;
