import React from 'react'
import './styles.css'
import Button from "react-mdl"
import ReactTable from "react-table-v6"
import "react-table-v6/react-table.css";
import { TextField } from '@material-ui/core';
import {getDoctors, getPatients} from "../../actions/admin"
import {Table, TableHeader} from "react-mdl"

class UserTable extends React.Component {

    constructor(props) {
        super(props)
        this.state= {
            patients: null,
            doctors: null,
            error: ""
        }
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handlePasswordChange = this.handlePasswordChange.bind(this)
        this.handleProvinceChange = this.handleProvinceChange.bind(this)
        this.handleAgeChange = this.handleAgeChange.bind(this)

    }

    componentDidMount() {
        getDoctors(this)
        getPatients(this)
    }

    render() {


        const material_list = this.state.patients.map((user)=>(
            {name: <input type="text" value={user.name} onChange={this.handleNameChange}/>, 
            Id: user.userId, 
            password: <input type="text" value={user.password} onChange={this.handlePasswordChange} />,
            province: <input type="text" value={user.province} onChange={this.handleProvinceChange} />,
            age: <input type="text" value={user.age} onChange={this.handleAgeChange} />
            }
          ))
        // const patient_columns = [
        //     {
        //         Header: 'User ID',
        //         accessor: '_id'
        //     },
        //     {
        //         Header: 'Username',
        //         accessor: 'username',
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handleNameChange}/>
        //         )
        //     },
        //     {
        //         Header: "Password",
        //         accessor: "password",
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handlePasswordChange}/>
        //         )
        //     },
        //     {
        //         Header: "Province",
        //         accessor: "province",
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handleProvinceChange}/>
        //         )
        //     },
        //     {
        //         Header: "Age",
        //         accessor: "age",
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handleAgeChange}/>
        //         )
        //     },
        //     {
        //         Header: 'Update',
        //         accessor: '_id',
        //         Cell: (props) => (
        //             <Button id={props.value} onClick={() => console.log("clicked!")}>
        //                 Update
        //             </Button>)
        //     }
        // ];

        // const doctor_columns = [
        //     {
        //         Header: 'User ID',
        //         accessor: '_id'
        //     },
        //     {
        //         Header: 'Username',
        //         accessor: 'username',
        //         Cell: (props) => (
        //             <TextField
        //             onChange={this.handleNameChange}
        //             label={props.value} 
        //             style={{width: "80%"}}/>
        //         )
        //     },
        //     {
        //         Header: "Password",
        //         accessor: "password",
        //         Cell: (props) => (
        //             <TextField
        //             onChange={this.handlePasswordChange}
        //             label="New Password..." 
        //             style={{width: "80%"}}/>
        //         )
        //     },
        //     {
        //         Header: "Province",
        //         accessor: "location",
        //         Cell: (props) => (
        //             <TextField
        //             onChange={this.handleProvinceChange}
        //             label={props.value} 
        //             style={{width: "80%"}}/>
        //         )
        //     },
        //     {
        //         Header: "Doctor Type",
        //         accessor: "doctorType",
        //         Cell: (props) => (
        //                 <TextField
        //                 onChange={() => console.log("typed")}
        //                 label={props.value} 
        //                 style={{width: "80%"}}/>
        //             )
        //     },
        //     {
        //         Header: 'Update',
        //         accessor: '_id',
        //         Cell: (props) => (
        //             <Button id={props.value} onClick={() => console.log("clicked!")}>
        //                 Update
        //             </Button>)
        //     }
        // ];
        // return (

        //         <ReactTable
        //             className={`-highlight`}
        //             columns={patient_columns}
        //             data={this.state.patients}
        //             noDataText={`No User currently`}
        //         >
        //         </ReactTable>
        // );

        return (

            <Table 
            className="table"
            sortable 
            shadow={1} 
            rows={material_list}
            >

                <TableHeader
                name="Id"
                sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The id of the users"
                >
                User Id
                </TableHeader>

                <TableHeader
                name="name"
                // sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The name of the users"
                >
                Name
                </TableHeader>

                <TableHeader
                name="password"
                // sortFn={(a, b, isAsc) => (isAsc ? a.value : b.value).match(/(.*)/)[0].localeCompare((isAsc ? b.value : a.value).match(/(.*)/)[0])}
                tooltip="The password of the users"
                >
                Password
                </TableHeader>

                <TableHeader
                name="province"
                // sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The password of the users"
                >
                Province
                </TableHeader>

                <TableHeader
                name="age"
                // sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The age of the users"
                >
                Age
                </TableHeader>
            </Table>
        )
    }

    handleNameChange(event) {
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.users.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i].userId === changed_id) {
                new_state[i].name = event.target.value
                break
            }
        }
        this.setState({users: new_state})
    }

    handlePasswordChange(event) {
        console.log(event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText)
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.users.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i].userId === changed_id) {
                new_state[i].password = event.target.value
                break
            }
        }
        this.setState({users: new_state})
    }

    handleProvinceChange(event) {
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.users.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i].userId === changed_id) {
                new_state[i].province = event.target.value
                break
            }
        }
        this.setState({users: new_state})
    }

    handleAgeChange(event) {
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.users.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i].userId === changed_id) {
                new_state[i].province = event.target.value
                break
            }
        }
        this.setState({users: new_state})
    }

}

export default UserTable
