export const displayMessage = (message, chatPage) => {
    const avatar = (chatPage.state.isDoctor) ? "https://img.icons8.com/wired/64/000000/medical-doctor.png" :
        "https://img.icons8.com/ios/50/000000/consultation.png";
    const newMessage = {
        "text": message,
        "id": chatPage.state.messages.length + 1,
        "sender": {
            "name": chatPage.state.name,
            "uid": chatPage.state.user.uid,
            "avatar": avatar,
        },
    };
    chatPage.state.messages.push(newMessage);
};
