import React from "react";
import { Button } from "reactstrap";
import { removePatientFromWatchlist } from "../../../actions/doctor";
class Patient extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      curr: this.props.currp,
      name: this.props.currp.username,
      userId: this.props.currp._id,
      province: this.props.currp.province,
      currDoctor: this.props.currd,
    };
  }
  removeAndupdate() {
    removePatientFromWatchlist(this.state.currDoctor, this.state.curr);
    for (let i = 0; i < this.state.currDoctor.watchlist.length; i++) {
      if (this.state.currDoctor.watchlist[i].username === this.state.name) {
        this.state.currDoctor.watchlist.splice(i, 1);
        break;
      }
    }
    console.log(this.state.currDoctor.watchlist);
    this.props.update(this.state.currDoctor.watchlist);
  }
  render() {
    return (
      <div className="Patient">
        <br></br> <br></br>
        <h3>UserName :{this.state.name}</h3>
        <p>User Id :{this.state.userId}</p>
        <p>Province :{this.state.province}</p>
        <Button
          color="warning"
          onClick={(event) => {
            this.removeAndupdate();
          }}
        >
          KICK OUT
        </Button>{" "}
        <br></br> <br></br>
      </div>
    );
  }
}

export default Patient;
