export const readAdminCookie = (app) => {
  const url = "/admins/check-session";

  fetch(url)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      if (json && json.currentAdmin) {
        app.setState({ currentAdmin: json.currentAdmin });
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

export const updateLoginForm = (loginComp, field) => {
  const value = field.value;
  const name = field.name;

  loginComp.setState({
    [name]: value,
  });
};

export const login = (loginComp, app) => {
  const request = new Request("/admins/login", {
    method: "post",
    body: JSON.stringify(loginComp.state),
    headers: {
      Accept: "application/json, text/plain, */*",
      "Content-Type": "application/json",
    },
  });

  fetch(request)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      } else {
        console.log(res.status)
        throw new Error()
      }
    })
    .then((json) => {
      console.log(json);
      if (json.currentAdmin !== null && json.currentAdmin.username !== undefined) {
        app.setState({ currentAdmin: json.currentAdmin });
      }
    })
    .catch((error) => {
      console.log(error);
      loginComp.setState({wrong: true})
    });
};

export const logout = (app) => {
  const url = "/logout";

  fetch(url)
    .then((res) => {
      app.setState({
        currentAdmin: null,
        message: { type: "", body: "" },
      });
    })
    .catch((error) => {
      console.log(error);
    });
};

export const getDoctors = (app) => {
  const url = "/doctors"

  fetch(url)
  .then((res) => {
    if (res.status === 200) {
      return res.json()
    } else {
      throw new Error()
    }
  }).then((json) => {
    if (json !== null && json !== undefined) {
      console.log("getDoctor before: ")
      console.log(app.state)
      app.setState({doctors: json})
      console.log("getDoctor after: ")
      console.log(app.state)
    } else {
      app.setState({error: "Cannot retrieve Doctor accounts information!"})
    }
  }).catch((e) => {
    app.setState({error: "Cannot retrieve Doctor accounts information!"})
  })
}

export const getPatients = (app) => {
  const url = "/patients"

  fetch(url)
  .then((res) => {
    if (res.status === 200) {
      return res.json()
    } else {
      throw new Error()
    }
  }).then((json) => {
    if (json !== null && json !== undefined) {
      console.log("getPatients before:")
      console.log(app.state)
      console.log(json)
      app.setState({patients: json})
      // app.setState({error: "success"})
      console.log("getPatients after:")
      console.log(app.state)
    } else {
      app.setState({error: "Cannot retrieve patient accounts information!"})
    }
  }).catch((e) => {
    app.setState({error: "Cannot retrieve patient accounts information!"})
  })
}
