import React from 'react'
import { Container, Row ,Col} from "reactstrap";
import CurrentNews from "../../../HomePage/currentnews"
import Cases from "../../../HomePage/Cases"
// import VisitHistory from '../VisitHistory/VisitHistory'
import { readPatientVisitHistory } from "../../../../actions/patients"
import "./ProfileTable.css"

class ProfileTable extends React.Component {

    constructor(props) {
        super(props)
        this.app = props.app
        this.state = {
            patient: this.props.patient,
            loading: true
        }
    }

    displayHistory() {
        if (this.state.patient.visit_history.length > 1) {
            const visits = this.state.patient.visit_history.map(function(visit, index) {
                if (index < 8) {
                    return (
                        <li key={uid(visit)}>
                            <h5>Dr. {visit.username} ({visit.doctorType})</h5>
                            <Button id={visit.username}
                                    onClick={(event) => setPatientChatPage(event.target.id, this.app, this.state.history)}>chat
                            </Button>
                        </li>
                    )
                }
            })
            return (
                <ul className="visit">
                    {visits}
                </ul>
            )
        } else {
            return(<h3>No visit history currently!</h3>)
        }
    }

    componentWillMount() {
        readPatientVisitHistory(this)
    }

    render() {
        const { history} = this.props.history;

        if (this.state.loading) {
            return (
                <div>
                <Container>
                    <div className="outer_box">
                        <h1 className="loading">Loading Dashboard, please wait!</h1>
                    </div>
                </Container>
                <div>
                <Container>
                    <CurrentNews/>
                </Container>
                </div>
            </div>
            )
        }
        else {
            return(
                <div>
                    <Container>
                        <div className="outer_box">
                            {/* <div className="profilePic">
                                <img alt=""/>
                            </div> */}
                            <div className="current_news">
                                <Cases p={this.state.province}/>
                            </div>
                            <div className="visit_history">
                                <Container className="listgrid">
                                    <div>
                                        <h3 className="historyHeader">Visit History:</h3>
                                        {this.displayHistory()}
                                    </div>
                                </Container>
                            </div>

                        </div>
                    </Container>
                    <div>
                    <Container>
                        <CurrentNews/>
                    </Container>
                    </div>
                </div>
            )
        }
    }

}

export default ProfileTable
