export const chatWithPatient = (waitList, patient) => {
    const filteredPatients = waitList.state.patients.filter(p => p !== patient);
    waitList.setState({
        patients: filteredPatients
    });
};

export const chatWithDoctor = (waitList, chatPage) => {
    let patientList = waitList.state.patients;
    const patient = {
        name: chatPage.state.name,
        id: chatPage.state.user.uid
    };
    patientList = patientList.filter(p => p.id !== patient.id);
    patientList.unshift(patient);
    waitList.setState({
        patients: patientList
    });
};

export const displayMessage = (message, chatPage) => {
    const avatar = (chatPage.state.doctor) ? "https://img.icons8.com/wired/64/000000/medical-doctor.png" :
        "https://img.icons8.com/ios/50/000000/consultation.png";
    const newMessage = {
        "text": message,
        "id": chatPage.state.messages.length + 1,
        "sender": {
            "name": chatPage.state.name,
            "uid": chatPage.state.user.uid,
            "avatar": avatar,
        },
    };
    chatPage.state.messages.push(newMessage);
};
