import React from "react";
import { Container } from "reactstrap";
import {DataTable, TableHeader} from "react-mdl"
import "./styles.css";

function ShowSearch(props) {
  console.log(props)
  if (props.found === {}) {
    console.log('a')
    return null
  }

  return (
    <div>
      <DataTable
        shadow={0}
        rows={[props.found]}>
        <TableHeader name="name" tooltip="The name of patients">Name</TableHeader>
        <TableHeader name="userId" tooltip="The id of patients">Id</TableHeader>
        <TableHeader name="province" tooltip="The province of patients">Province</TableHeader>
        <TableHeader name="add">action1</TableHeader>
      </DataTable>
    </div>
  )
}

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      keyword: "",
      all_user: props.users,
      found: {},
      search_result: null,
      patients: props.patients
    };
    this.handleClick = this.handleClick.bind(this)
    this.handleAdd = this.handleAdd.bind(this)
  }

  handleChange = (event) => {
    this.setState({
      keyword: event.target.value,
    });
  };

  handleAdd(event) {

  }

  handleClick(event) {
    this.setState({found: {}})
    // check if name or id matches
    for (let i = 0; i < this.state.all_user.length; i++) {
      if (this.state.all_user[i].name === this.state.keyword || this.state.all_user[i].userId === this.state.keyword) {
        this.setState({found: {name: this.state.all_user[i].name, 
          userId: this.state.all_user[i].userId, 
          province: this.state.all_user[i].province,
          add: <button onClick={this.handleAdd}>add to watchlist</button>}
          })
        break
      }
    }
  }

  render() {
    return (
      <Container>
        <div className="SearchBar">
          <label>
            Search Patient:
            <input
              className="input"
              type="text"
              placeholder="Search a patient by name/id"
              value={this.state.keyword}
              onChange={this.handleChange}
            />
            <ShowSearch found={this.state.found} />
            <button
              className="button"
              type="text"
              value="Search"
              onClick={this.handleClick}
            >Search</button>
          </label>
        </div>
      </Container>

    );
  }
}

export default SearchBar;
