import React from "react";
import {ChatBox} from 'react-chatbox-component';
import 'react-chatbox-component/dist/style.css';
import {displayMessage} from "./actions/ChatActions";
import PatientHeader from "../PatientDashboard/components/PatientHeader/PatientHeader";
class PatientChatPage extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            name: "xxx",
            messages: [],
            user: {"uid": "user101"},
            doctor: false,
        };
    }
    componentDidMount() {
        const messages = [
            {
                "text": "Hi, anything I can help you?",
                "id": "1",
                "sender": {
                    "name": "Dr.yyy",
                    "uid": "user123",
                    "avatar": "https://img.icons8.com/wired/64/000000/medical-doctor.png",
                }
            }];
        this.setState({messages:messages});
    }
    render() {
        return (
            <div>
                <PatientHeader name={this.state.name}/>
                <div className='dmain'>
                    <div className='dcontainer'>
                        <div className='chat-header'>
                            <h5>Welcome {this.state.name}</h5>
                        </div>
                        <ChatBox messages={this.state.messages} user={this.state.user} onSubmit={(message) =>
                            displayMessage(message, this)}
                        />
                    </div>
                    <div className='dinfo'>
                        <p className='dinfo-p'>Doctor info</p>
                        <table className='dinfo-table'>
                            <tbody>
                            <tr>
                                <td>Name:</td>
                                <td>Dr.yyy</td>
                            </tr>
                            <tr>
                                <td>Type:</td>
                                <td>Family Physician</td>
                            </tr>
                            <tr>
                                <td>Location:</td>
                                <td>Mississauga</td>
                            </tr>
                            </tbody>
                        </table>
                    </div>
                </div>
            </div>
        )
    }
}

export default PatientChatPage;
