export const readAdminCookie = (app) => {
  const url = "/admins/check-session";

  fetch(url)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      if (json && json.currentAdmin) {
        app.setState({ currentAdmin: json.currentAdmin });
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

export const updateLoginForm = (loginComp, field) => {
  const value = field.value;
  const name = field.name;

  loginComp.setState({
    [name]: value,
  });
};

export const login = (loginComp, app) => {
  const request = new Request("/admins/login", {
    method: "post",
    body: JSON.stringify(loginComp.state),
    headers: {
      Accept: "application/json, text/plain, */*",
      "Content-Type": "application/json",
    },
  });

  fetch(request)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      console.log(json);
      if (json.currentAdmin !== undefined) {
        app.setState({ currentAdmin: json.currentAdmin });
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

export const logout = (app) => {
  const url = "/logout";

  fetch(url)
    .then((res) => {
      app.setState({
        currentAdmin: null,
        message: { type: "", body: "" },
      });
    })
    .catch((error) => {
      console.log(error);
    });
};
