import React from "react";
import { ChatBox } from "react-chatbox-component";
import LoginHeader from "../DoctorDashboard/LoginHeader/LoginHeader";
import "react-chatbox-component/dist/style.css";
import { displayMessage } from "./actions/ChatActions";
import "./styles.css";

class DoctorChatPage extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      name: "Dr.yyy",
      messages: [],
      user: { uid: "user123" },
      doctor: true,
    };
  }
  componentDidMount() {
    const messages = [
      {
        "text": "Hello Doctor",
        "id": "1",
        "sender": {
          "name": "xxx",
          "uid": "user101",
          "avatar": "https://img.icons8.com/ios/50/000000/consultation.png",
        }
      }];
    this.setState({messages:messages});
  }
  render() {
    return (
      <div>
        <LoginHeader name={this.state.name} />
        <div className="dmain">
          <div className="dcontainer">
            <div className="chat-header">
              <h5>Welcome {this.state.name}</h5>
            </div>
            <ChatBox
              messages={this.state.messages}
              user={this.state.user}
              onSubmit={(message) => displayMessage(message, this)}
            />
          </div>
          <div className="dinfo">
            <p className="dinfo-p">Patient info</p>
            <table className="dinfo-table">
              <tbody>
                <tr>
                  <td>Name:</td>
                  <td>xxx</td>
                </tr>
                <tr>
                  <td>Gender:</td>
                  <td>Male</td>
                </tr>
                <tr>
                  <td>Age:</td>
                  <td>21</td>
                </tr>
                <tr>
                  <td>Location:</td>
                  <td>Markham</td>
                </tr>
              </tbody>
            </table>
              <br/>
              <br/>
              <br/>
              <p className={"dinfo-p"}>Symptom Checklist</p>
            <table className={"dinfo-table"}>
              <tbody>
                <tr>
                  <td>temperature:</td>
                  <td>36.5</td>
                </tr>
                <tr>
                  <td>nausea:</td>
                  <td>None</td>
                </tr>
                <tr>
                  <td>stomachache:</td>
                  <td>None</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default DoctorChatPage;
