import React from "react";
import { ChatBox } from "react-chatbox-component";
import LoginHeader from "../DoctorDashboard/LoginHeader/LoginHeader";
import "react-chatbox-component/dist/style.css";
import { displayMessage } from "./actions/ChatActions";
import "./styles.css";
import {getMessages} from "../../actions/message";

class DoctorChatPage extends React.Component {
  constructor(props) {
    super(props);
    this.props.history.push("/ChatPage/DoctorChatPage");
    this.state = {
      name: this.props.app.state.currentDoctor.username,
      messages: [],
      user: {"uid": '1'},
      patient: this.props.app.state.contactPatient.patient,
      isDoctor: true
    };
  }
  componentDidMount() {
    getMessages(this);
  }
  render() {
    const {history, app} = this.props;
    return (
      <div>
        <LoginHeader history={history} app={app} name={this.state.username}/>
        <div className="dmain">
          <div className="dcontainer">
            <div className="chat-header">
              <h5>Welcome {this.state.name}</h5>
            </div>
            <ChatBox
              messages={this.state.messages}
              user={this.state.user}
              onSubmit={(message) => displayMessage(message, this)}
            />
          </div>
          <div className="dinfo">
            <p className="dinfo-p">Patient info</p>
            <table className="dinfo-table">
              <tbody>
                <tr>
                  <td>Name:</td>
                  <td>{this.state.patient.username}</td>
                </tr>
                <tr>
                  <td>Gender:</td>
                  <td>{this.state.patient.gender}</td>
                </tr>
                <tr>
                  <td>Age:</td>
                  <td>{this.state.patient.age}</td>
                </tr>
                <tr>
                  <td>Location:</td>
                  <td>{this.state.patient.province}</td>
                </tr>
              </tbody>
            </table>
              <br/>
              <br/>
              <br/>
              <p className={"dinfo-p"}>Symptom Checklist</p>
            <table className={"dinfo-table"}>
              <tbody>
                <tr>
                  <td>temperature:</td>
                  <td>36.5</td>
                </tr>
                <tr>
                  <td>nausea:</td>
                  <td>None</td>
                </tr>
                <tr>
                  <td>stomachache:</td>
                  <td>None</td>
                </tr>
              </tbody>
            </table>
          </div>
        </div>
      </div>
    );
  }
}

export default DoctorChatPage;
