import React from 'react'
import './styles.css'
import "react-table-v6/react-table.css";
import {getDoctors} from "../../actions/admin"
import {Table, TableHeader, Tab, Tabs} from "react-mdl"

class UserTable extends React.Component {

    constructor(props) {
        super(props)
        this.state= {
            doctors: [],
            error: "",
            activeTab: 0
        }
        this.handleNameChange = this.handleNameChange.bind(this)
        this.handlePasswordChange = this.handlePasswordChange.bind(this)
        this.handleLocationChange = this.handleProvinceChange.bind(this)
        this.handleDoctorTypeChange = this.handleDoctorTypeChange.bind(this)

    }

    componentWillMount() {
        getDoctors(this)
    }

    render() {

        const doctor_list = this.state.doctors.map((user)=>(
            {name: <input type="text" value={user.username} onChange={this.handleDoctorNameChange}/>, 
            Id: user._id, 
            password: <input type="text" value={"Enter new password"} onChange={this.handleDoctorPasswordChange} />,
            location: <input type="text" value={user.location} onChange={this.handleLocationChange} />,
            doctorType: <input type="text" value={user.doctorType} onChange={this.handleDoctorTypeChange} />,
            edit: <button onClick={()=> console.log("clicked")} >Edit</button>
            }
          ))
        // const patient_columns = [
        //     {
        //         Header: 'User ID',
        //         accessor: '_id'
        //     },
        //     {
        //         Header: 'Username',
        //         accessor: 'username',
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handleNameChange}/>
        //         )
        //     },
        //     {
        //         Header: "Password",
        //         accessor: "password",
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handlePasswordChange}/>
        //         )
        //     },
        //     {
        //         Header: "Province",
        //         accessor: "province",
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handleProvinceChange}/>
        //         )
        //     },
        //     {
        //         Header: "Age",
        //         accessor: "age",
        //         Cell: (props) => (
        //             <input type="text" value={props.value} onChange={this.handleAgeChange}/>
        //         )
        //     },
        //     {
        //         Header: 'Update',
        //         accessor: '_id',
        //         Cell: (props) => (
        //             <Button id={props.value} onClick={() => console.log("clicked!")}>
        //                 Update
        //             </Button>)
        //     }
        // ];

        // const doctor_columns = [
        //     {
        //         Header: 'User ID',
        //         accessor: '_id'
        //     },
        //     {
        //         Header: 'Username',
        //         accessor: 'username',
        //         Cell: (props) => (
        //             <TextField
        //             onChange={this.handleNameChange}
        //             label={props.value} 
        //             style={{width: "80%"}}/>
        //         )
        //     },
        //     {
        //         Header: "Password",
        //         accessor: "password",
        //         Cell: (props) => (
        //             <TextField
        //             onChange={this.handlePasswordChange}
        //             label="New Password..." 
        //             style={{width: "80%"}}/>
        //         )
        //     },
        //     {
        //         Header: "Province",
        //         accessor: "location",
        //         Cell: (props) => (
        //             <TextField
        //             onChange={this.handleProvinceChange}
        //             label={props.value} 
        //             style={{width: "80%"}}/>
        //         )
        //     },
        //     {
        //         Header: "Doctor Type",
        //         accessor: "doctorType",
        //         Cell: (props) => (
        //                 <TextField
        //                 onChange={() => console.log("typed")}
        //                 label={props.value} 
        //                 style={{width: "80%"}}/>
        //             )
        //     },
        //     {
        //         Header: 'Update',
        //         accessor: '_id',
        //         Cell: (props) => (
        //             <Button id={props.value} onClick={() => console.log("clicked!")}>
        //                 Update
        //             </Button>)
        //     }
        // ];
        // return (
        //         <ReactTable
        //             className={`-highlight`}
        //             columns={patient_columns}
        //             data={this.state.patients}
        //             noDataText={`No User currently`}
        //         >
        //         </ReactTable>
        // );


        return (

            <Table 
            sortable 
            shadow={1} 
            rows={doctor_list}
            >

                <TableHeader
                name="Id"
                sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The id of the users"
                >
                User Id
                </TableHeader>

                <TableHeader
                name="name"
                sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The name of the users"
                >
                Name
                </TableHeader>

                <TableHeader
                name="password"
                // sortFn={(a, b, isAsc) => (isAsc ? a.value : b.value).match(/(.*)/)[0].localeCompare((isAsc ? b.value : a.value).match(/(.*)/)[0])}
                tooltip="The password of the users"
                >
                Password
                </TableHeader>

                <TableHeader
                name="location"
                // sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The province of the users"
                >
                Province
                </TableHeader>

                <TableHeader
                name="doctorType"
                // sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="The field the user specializes"
                >
                Age
                </TableHeader>

                <TableHeader
                name="edit"
                // sortFn={(a, b, isAsc) => (isAsc ? a : b).match(/(.*)/)[0].localeCompare((isAsc ? b : a).match(/(.*)/)[0])}
                tooltip="Click the edit button to update the user's information"
                >
                Edit
                </TableHeader>
            </Table>

        )
    }

    handleNameChange(event) {
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.doctors.slice()
        console.log(new_state[0]._id)
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i]._id === changed_id) {
                new_state[i].username = event.target.value
                break
            }
        }
        this.setState({doctors: new_state})
    }

    handlePasswordChange(event) {
        console.log(event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText)
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.doctors.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i]._id === changed_id) {
                new_state[i].password = event.target.value
                break
            }
        }
        this.setState({doctors: new_state})
    }

    handleLocationChange(event) {
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.doctors.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i]._id === changed_id) {
                new_state[i].province = event.target.value
                break
            }
        }
        this.setState({doctors: new_state})
    }

    handleDoctorTypeChange(event) {
        const changed_id= event.target.parentElement.parentElement.querySelectorAll("td")[1].innerText
        const new_state= this.state.doctors.slice()
        for (let i = 0; i < new_state.length; i++) {
            if (new_state[i]._id === changed_id) {
                new_state[i].doctorType = event.target.value
                break
            }
        }
        this.setState({doctors: new_state})
    }

}

export default UserTable
