export const readDoctorCookie = (app) => {
  const url = "/doctors/check-session";

  fetch(url)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      if (json && json.currentDoctor) {
        app.setState({ currentDoctor: json.currentDoctor });
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

export const updateLoginForm = (loginComp, field) => {
  const value = field.value;
  const name = field.name;

  loginComp.setState({
    [name]: value,
  });
};

export const login = (loginComp, app) => {
  const request = new Request("/doctors/login", {
    method: "post",
    body: JSON.stringify(loginComp.state),
    headers: {
      Accept: "application/json, text/plain, */*",
      "Content-Type": "application/json",
    },
  });

  fetch(request)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      console.log(json);
      if (json.currentDoctor !== undefined) {
        app.setState({ currentDoctor: json.currentDoctor });
      }
    })
    .catch((error) => {
      console.log(error);
      loginComp.setState({wrong: true})
    });
};

export const logout = (app) => {
  const url = "/logout";

  fetch(url)
    .then((res) => {
      app.setState({
        currentDoctor: null,
        message: { type: "", body: "" },
      });
    })
    .catch((error) => {
      console.log(error);
    });
};

export const getDoctors = (ppc) => {
  const url = "/doctors";
  fetch(url)
      .then((res) => {
        if (res.status === 200) {
          return res.json();
        }
      })
      .then((json) => {
        ppc.setState({
          doctorInfo: json
        })
      })
      .catch((error) => {
        console.log(error);
      });
}
