import React, { Component } from "react";
import { Textfield, Button } from "react-mdl";
import { Container } from "reactstrap";
import "./login.css";
import Navbar from "../HomePage/Navbar";
class Login extends Component {
  state = { wrongpw: false, username: "", password: "" };

  handleChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  handleincorrect = (event) => {
    event.preventDefault();
    this.setState({ wrongpw: true });
  };
  handleSignIn = () => {
    if (this.state.username === "user" && this.state.password === "user") {
      return (
        <Button raised colored href="/DoctorHome">
          Sign in
        </Button>
      );
    } else if (
      this.state.username === "admin" &&
      this.state.password === "admin"
    ) {
      return (
        <Button raised colored href="/AdminHome">
          Sign in
        </Button>
      );
    } else {
      return (
        <Button raised colored onClick={this.handleincorrect}>
          Sign in
        </Button>
      );
    }
  };
  render() {
    return (
      <div class="entire_background">
        <Navbar />
        <Container className="content">
          <h1 className="logintitle">Doctor Login</h1>
          <hr />
          <div className="Login">
            <form>
              Username:{" "}
              <Textfield
                name="username"
                onChange={this.handleChange}
                label="Please enter your username"
              />{" "}
              <br />
              Password:{" "}
              <Textfield
                name="password"
                onChange={this.handleChange}
                label="please enter your password"
              />{" "}
              <br />
              {this.state.wrongpw ? (
                <div class="alert alert-danger" role="alert">
                  Your username or password is incorrect, please try again.
                </div>
              ) : null}
              {this.handleSignIn()}
            </form>
          </div>
        </Container>
      </div>
    );
  }
}

export default Login;
