export const readPatientCookie = (app) => {
  const url = "/patients/check-session";

  fetch(url)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      if (json && json.currentPatient) {
        app.setState({ currentPatient: json.currentPatient });
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

export const updateLoginForm = (loginComp, field) => {
  const value = field.value;
  const name = field.name;

  loginComp.setState({
    [name]: value,
  });
};

export const login = (loginComp, app) => {
  const request = new Request("/patients/login", {
    method: "post",
    body: JSON.stringify(loginComp.state),
    headers: {
      Accept: "application/json, text/plain, */*",
      "Content-Type": "application/json",
    },
  });

  fetch(request)
    .then((res) => {
      if (res.status === 200) {
        return res.json();
      }
    })
    .then((json) => {
      console.log(json);
      if (json.currentPatient !== undefined) {
        app.setState({ currentPatient: json.currentPatient });
      }
    })
    .catch((error) => {
      console.log(error);
    });
};

export const register = (registerComp) => {
  const url = "/patients";

  const patient = registerComp.state;

  const request = new Request(url, {
    method: "post",
    body: JSON.stringify(patient),
    headers: {
      Accept: "application/json, text/plain, */*",
      "Content-type": "application/json",
    },
  });

  console.log(request);
  console.log(JSON.stringify(patient));

  fetch(request)
    .then(function (res) {
      if (res.status !== 200) {
        registerComp.setState({
          patient_registered: "Patient cannot be registered at the moment!",
        });
      } else {
        registerComp.setState({ patient_registered: "Patient account added!" });
      }
    })
    .catch((e) => {
      console.log(e);
    });
};

export const logout = (app) => {
  const url = "/patients/logout";

  fetch(url)
    .then((res) => {
      app.setState({
        currentPatient: null,
        message: { type: "", body: "" },
      });
    })
    .catch((error) => {
      console.log(error);
    });
};
