import React from "react";
import Button from "@material-ui/core/Button";
import TableCell from "@material-ui/core/TableCell";
import TableRow from "@material-ui/core/TableRow";
import { Link } from "react-router-dom";

import {chatWithPatient} from "../actions/ChatActions";

class Patient extends React.Component {
    constructor(props) {
        super(props);
        this.state = {
            seconds: 0
        };
    }

    componentDidMount() {
        // When the component enters the DOM
        this.patientTimer = setInterval(() => this.tick(), 1000);
    }

    componentWillUnmount() {
        clearInterval(this.patientTimer);
    }

    tick() {
        this.setState({
            seconds: this.state.seconds + 1
        });
    }

    render() {
        const { patient, queueComponent } = this.props;

        return (
            <TableRow className="patient" key={patient.name}>
                <TableCell component="th" scope="row">
                    {patient.name}
                </TableCell>

                <TableCell component="th" scope="row">
                    <Link to={"/ChatPage/DoctorChatPage"}>
                        <Button
                            variant="contained"
                            color="secondary"
                            onClick={
                                chatWithPatient.bind(this, queueComponent, patient)
                            }
                        >
                            Chat
                        </Button>
                    </Link>
                </TableCell>

                <TableCell component="th" scope="row">
                    User ID: {patient.id}
                </TableCell>

                <TableCell component="th" scope="row">
                    Message from {Math.floor(this.state.seconds / 60)} minutes{" "}
                    {this.state.seconds % 60} seconds ago.
                </TableCell>

            </TableRow>
        );
    }
}

export default Patient;
