import React from "react";
import { Container } from "reactstrap";
import { DataTable, TableHeader, Button} from "react-mdl";
import { getPatients } from "../../../actions/doctor"
import Patient from "../Patient/Patient";
import "./styles.css";

class SearchBar extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      keyword: "",
      found: [],
      patients: [],
      watchlist: props.app.state.currentDoctor.watchlist,
    };
    this.handleClick = this.handleClick.bind(this);
  }

  handleChange = (event) => {
    this.setState({
      keyword: event.target.value,
    });
  };

  showSearch(found_patients) {

    return (
      <div>
        <br></br> <br></br>
        <DataTable className="dtable" shadow={0} rows={found_patients}>
          <TableHeader name="userId" tooltip="The id of patients">
            Id
          </TableHeader>
          <TableHeader name="name" tooltip="The name of patients">
            Name
          </TableHeader>
          <TableHeader name="province" tooltip="The province of patient(s)">
            Province
          </TableHeader>
          <TableHeader name="age" tooltip="The age of patient(s)">
            Age
          </TableHeader>
          <TableHeader name="province" tooltip="The gender of patient(s)">
            Gender
          </TableHeader>
          <TableHeader name="add">Add</TableHeader>
        </DataTable>
      </div>
    );
  }

  handleClick() {
    // check if name or id matches
    getPatients(this)
  }

  render() {
    const {app} = this.props

    let found_patients = []
    if (this.state.keyword.length < 1) {
      found_patients =  this.state.patients.map((user) => {
        return (
        {name: `${user.username}`, 
        Id: `${user._id}`, 
        province: `${user.province}`,
        age: `${user.age}`,
        add: <Button onClick={()=> console.log("clicked")} >Add</Button>
        }
        )
        })
    } else {
      found_patients = this.patients.filter((patient) => {
        if (patient._id === this.state.keyword || patient.username === this.state.keyword) {
          found_patients.push(patient)
        }
      })
      found_patients = found_patients.map((user) => {
        return (
          {name: `${user.username}`, 
          Id: `${user._id}`, 
          province: `${user.province}`,
          age: `${user.age}`,
          add: <Button onClick={()=> console.log("clicked")} >Add</Button>
        }
        )
      })
    }
    console.log(found_patients)

    return (
      <Container>
        <div className="SearchBar">
          <label>
            Search Patient:
            <input
              className="input"
              type="text"
              placeholder="Search a patient by username or id"
              value={this.state.keyword}
              onChange={this.handleChange}
            />
            <button
              className="button"
              type="text"
              value="Search"
              onClick={() => this.handleClick()}
            >
              Search
            </button>
            {this.showSearch(found_patients)}
          </label>
        </div>
        <ul>
          {app.state.currentDoctor.watchlist.map((patient) => (
            <li key={patient.userId}>
              <Patient
                key={patient.userId}
                name={patient.name}
                userId={patient.userId}
                checkList={patient.checklist}
                province={patient.province}
              />
              <br></br>
              <br></br>
            </li>
          ))}
        </ul>
      </Container>
    );
  }
}

export default SearchBar;
