import React from 'react'
import {uid} from 'react-uid'
import { Container, Button } from 'reactstrap'
import {setPatientChatPage} from "../../../../actions/message";
import {Link} from "react-router-dom";


class VisitHistory extends React.Component {

    constructor(props) {
        super(props)
        this.state = {
            history: props.visits
        }
    }

    displayHistory() {
        if (this.state.history.length > 1) {
            const history = this.state.history.map(function(visit) {
                return (
                    <li key={uid(visit)}>
                        <h5>Dr. {visit.username} ({visit.doctorType})</h5>
                        {/* <h6>Visit date: {visit.visit_date.getFullYear()}.{visit.visit_date.getMonth()+1}.{visit.visit_date.getDate()}</h6>
                        <p>Guidance: {visit.guidance ? visit.guidance : null} </p> */}
                        <button id={visit.username}
                                onClick={(event) => setPatientChatPage(event.target.id, app, history)}>chat
                        </button>
                    </li>
                )
            })
            return (
                <ul className="visit">
                    {history}
                </ul>
            )
        } else {
            return(<h3>No visit history currently!</h3>)
        }
    }

    render() {
        const {app, history} = this.props;
        return (
            <Container className="listgrid">
                <div>
                    <h3 className="historyHeader">Visit History:</h3>
                    {this.displayHistory()}
                </div>
            </Container>
        )
    }

}

export default VisitHistory
