export const readDoctorCookie = (app) => {
    const url = "/doctors/check-session";
    fetch(url)
        .then((res) => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .then((json) => {
            if (json && json.currentDoctor) {
                app.setState({currentDoctor: json.currentDoctor});
            }
        })
        .catch((error) => {
            console.log(error);
        });
};

export const readSelectedDoctorCookie = (app) => {
    const url = "/doctors/contact-session";
    fetch(url)
        .then((res) => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .then((json) => {
            if (json && json.selectedDoctor) {
                app.setState({selectedDoctor: json.selectedDoctor});
            }
        })
        .catch((error) => {
            console.log(error);
        });
};


export const updateLoginForm = (loginComp, field) => {
    const value = field.value;
    const name = field.name;

    loginComp.setState({
        [name]: value,
    });
};

export const login = (loginComp, app) => {
    const request = new Request("/doctors/login", {
        method: "post",
        body: JSON.stringify(loginComp.state),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json",
        },
    });

    fetch(request)
        .then((res) => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .then((json) => {
            console.log(json);
            if (json.currentDoctor !== undefined) {
                app.setState({currentDoctor: json.currentDoctor});
            }
        })
        .catch((error) => {
            console.log(error);
            loginComp.setState({wrong: true})
        });
};

export const logout = (app) => {
    const url = "/logout";

    fetch(url)
        .then((res) => {
            app.setState({
                currentDoctor: null,
                message: {type: "", body: ""},
            });
        })
        .catch((error) => {
            console.log(error);
        });
};

export const getDoctors = (ppc) => {
    const url = "/doctors";
    fetch(url)
        .then((res) => {
            if (res.status === 200) {
                return res.json();
            }
        })
        .then((json) => {
            ppc.setState({
                doctorInfo: json
            })
        })
        .catch((error) => {
            console.log(error);
        });
};

export const addPatientToWatchlist = (button, doctor, patient) => {
    const url = "/doctors";
    console.log(patient);
    console.log(doctor);
    const request = new Request(url, {
        method: "put",
        body: JSON.stringify({
            patient: patient,
            doctor: doctor
        }),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json",
        },
    });
    fetch(request).then(res => {
        if (res.status === 200) {
            button.innerText = 'Added!';
            setTimeout(function () {
                button.innerText = 'Add to List'
            }, 1000)
        } else {
            button.innerText = 'Failed!';
            setTimeout(function () {
                button.innerText = 'Add to List'
            }, 1000)
        }
    }).catch(error => {
        console.log(error)
    })

};

export const updateWatchlist = (app, button, doctor, patient) => {
    const url = "/doctors";
    console.log(patient);
    console.log(doctor);
    const request = new Request(url, {
        method: "put",
        body: JSON.stringify({
            patient: patient,
            doctor: doctor
        }),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-Type": "application/json",
        },
    });
    fetch(request).then(res => {
        if (res.status === 200) {
            return res.json()
        } else {
            throw new Error()
        }
    }).then((json) => {
        if (json !== undefined && json !== null) {
            button.innerText = 'Added!';
            button.disabled = true;
            app.setState({watchlist: json.watchlist})
        } else {
            throw new Error()
        }
    })
    .catch(error => {
        button.innerText = 'Failed!';
        button.disabled = true
        setTimeout(function () {
            button.innerText = 'Add'
            button.disabled = false
        }, 1000)
    })

};

export const endDoctorChat = (app) => {
    const url = "/patients/contact-session";
    const request = new Request(url, {
        method: "put",
        body: JSON.stringify({
            selectedPatient: null
        }),
        headers: {
            Accept: "application/json, text/plain, */*",
            "Content-type": "application/json",
        },
    });
    fetch(request).then(res => {
        if (res.status === 200) {
            return res.json()
        }
    }).then(json => {
        app.setState(
            {
                selectedPatient: json.selectedPatient
            })
    })
        .catch(error => {
            console.log(error);
        });
};

export const getPatients = (app) => {
    const url = "/patients"

    fetch(url)
    .then((res) => {
      if (res.status === 200) {
        return res.json()
      } else {
        throw new Error()
      }
    }).then((json) => {
      if (json !== null && json !== undefined) {
          console.log(json)
        app.setState({patients: json})
        // app.setState({error: "success"})
      } else {
        app.setState({error: "Cannot retrieve patient accounts information!"})
      }
    }).catch((e) => {
        console.log("error caught")
      app.setState({error: "Cannot retrieve patient accounts information!"})
    })
  }

