import React from "react";
import {
  DataTable,
  TableHeader,
  Textfield,
  RadioGroup,
  Radio,
  Button,
} from "react-mdl";
import Users from "../HomePage/def/users.json";
import PatientHeader from "../PatientDashboard/components/PatientHeader/PatientHeader";
import "./SymptomChecklist.css";

class SymptomChecklist extends React.Component {
  constructor() {
    super();
    this.state = {
      list: Users.patients[0].checkList.list,
      date: new Date(),
    };
    this.handleTemperatureChange = this.handleTemperatureChange.bind(this);
    this.handleStomachacheChange = this.handleStomachacheChange.bind(this);
    this.handleNauseaChange = this.handleNauseaChange.bind(this);
  }

  render() {
    return (
      <div>
        <PatientHeader name={Users.patients[0].name} />

        <form className="table">
          <DataTable
            className="table"
            shadow={1}
            rows={[
              {
                symptom: "Temperature",
                option: (
                  <Textfield
                    onChange={this.handleTemperatureChange}
                    label="36.5°C"
                    pattern="[0-9]*(\.[0-9]+)?(°C)?"
                    error="Please enter a valid temperature"
                  />
                ),
              },
              {
                symptom: "Nausea",
                option: (
                  <RadioGroup name="nausea" value="None">
                    <Radio
                      value="None"
                      ripple
                      onClick={this.handleNauseaChange}
                    >
                      None
                    </Radio>
                    <Radio
                      value="Mild"
                      ripple
                      onClick={this.handleNauseaChange}
                    >
                      Mild
                    </Radio>
                    <Radio
                      value="intense"
                      ripple
                      onClick={this.handleNauseaChange}
                    >
                      Intense
                    </Radio>
                    <Radio
                      value="severe"
                      ripple
                      onClick={this.handleNauseaChange}
                    >
                      Severe
                    </Radio>
                  </RadioGroup>
                ),
              },
              {
                symptom: "Stomachache",
                option: (
                  <RadioGroup name="stomachache" value="None">
                    <Radio
                      value="None"
                      ripple
                      onClick={this.handleStomachacheChange}
                    >
                      None
                    </Radio>
                    <Radio
                      value="Mild"
                      ripple
                      onClick={this.handleStomachacheChange}
                    >
                      Mild
                    </Radio>
                    <Radio
                      value="intense"
                      ripple
                      onClick={this.handleStomachacheChange}
                    >
                      Intense
                    </Radio>
                    <Radio
                      value="severe"
                      ripple
                      onClick={this.handleStomachacheChange}
                    >
                      Severe
                    </Radio>
                  </RadioGroup>
                ),
              },
            ]}
          >
            <TableHeader
              name="symptom"
              tooltip="The possible symptoms of patient"
            >
              Symptoms
            </TableHeader>
            <TableHeader name="option" tooltip="Options for the symptoms">
              Options
            </TableHeader>
          </DataTable>
          <Button raised colored>
            Submit
          </Button>
        </form>
      </div>
    );
  }

  handleTemperatureChange(event) {
    const new_list = this.state.list.slice();
    new_list[0] = event.target.value;
    this.setState({ list: new_list });
  }

  handleNauseaChange(event) {
    const new_list = this.state.list.slice();
    new_list[1] = event.target.value;
    this.setState({ list: new_list });
  }

  handleStomachacheChange(event) {
    const new_list = this.state.list.slice();
    new_list[2] = event.target.value;
    this.setState({ list: new_list });
  }
}

export default SymptomChecklist;
